const items = $input.all();

// Group items by source based on link domain
const marketwatch = [];
const wsj = [];
const investing = [];
const cnbc = [];
const yahoo = [];
const nasdaq = [];

for (const item of items) {
  const data = item.json;
  const link = data.link || '';

  const post = {
    title: data.title || '',
    summary: (data.contentSnippet || data.content || '').replace(/<[^>]*>/g, '').slice(0, 300),
    link: link,
    pubDate: data.pubDate || data.isoDate || ''
  };

  if (link.includes('marketwatch.com')) {
    post.source = 'MarketWatch';
    marketwatch.push(post);
  } else if (link.includes('wsj.com') || link.includes('dowjones')) {
    post.source = 'WSJ';
    wsj.push(post);
  } else if (link.includes('investing.com')) {
    post.source = 'Investing.com';
    investing.push(post);
  } else if (link.includes('cnbc.com')) {
    post.source = 'CNBC';
    cnbc.push(post);
  } else if (link.includes('yahoo.com') || link.includes('finance.yahoo')) {
    post.source = 'Yahoo Finance';
    yahoo.push(post);
  } else if (link.includes('nasdaq.com')) {
    post.source = 'Nasdaq';
    nasdaq.push(post);
  }
}

const totalCount = marketwatch.length + wsj.length + investing.length + cnbc.length + yahoo.length + nasdaq.length;

return [{
  json: {
    marketwatch: JSON.stringify(marketwatch.slice(0, 10), null, 2),
    marketwatchCount: Math.min(marketwatch.length, 10),
    wsj: JSON.stringify(wsj.slice(0, 10), null, 2),
    wsjCount: Math.min(wsj.length, 10),
    investing: JSON.stringify(investing.slice(0, 10), null, 2),
    investingCount: Math.min(investing.length, 10),
    cnbc: JSON.stringify(cnbc.slice(0, 10), null, 2),
    cnbcCount: Math.min(cnbc.length, 10),
    yahoo: JSON.stringify(yahoo.slice(0, 10), null, 2),
    yahooCount: Math.min(yahoo.length, 10),
    nasdaq: JSON.stringify(nasdaq.slice(0, 10), null, 2),
    nasdaqCount: Math.min(nasdaq.length, 10),
    totalCount: totalCount,
    error: totalCount === 0,
    errorMsg: totalCount === 0 ? 'No articles fetched' : ''
  }
}];
