const response = $input.first().json;

try {
  const xmlText = typeof response === 'string' ? response : (response.data || '');

  const items = [];
  const itemRegex = /<item>(.*?)<\/item>/gs;
  let match;

  while ((match = itemRegex.exec(xmlText)) !== null && items.length < 10) {
    const itemXml = match[1];

    const getTag = (xml, tag) => {
      const regex = new RegExp(`<${tag}[^>]*><!\\[CDATA\\[([\\s\\S]*?)\\]\\]><\/${tag}>|<${tag}[^>]*>([\\s\\S]*?)<\/${tag}>`, 'i');
      const m = xml.match(regex);
      return m ? (m[1] || m[2] || '').trim() : '';
    };

    const title = getTag(itemXml, 'title');
    const link = getTag(itemXml, 'link');
    const description = getTag(itemXml, 'description').replace(/<[^>]*>/g, '').slice(0, 200);
    const author = getTag(itemXml, 'author') || getTag(itemXml, 'dc:creator');
    const category = getTag(itemXml, 'category');

    if (title && link) {
      items.push({
        title: title,
        summary: description,
        company: author || 'Velopers',
        tags: category ? [category] : [],
        link: link
      });
    }
  }

  return [{ json: { source: 'velopers', posts: items, count: items.length, error: false, errorMsg: '' } }];
} catch (e) {
  return [{ json: { source: 'velopers', posts: [], count: 0, error: true, errorMsg: e.message } }];
}

