    const pointsMatch = description.match(/Points:\s*(\d+)/);
    const points = pointsMatch ? pointsMatch[1] : '';

    if (title && link && !title.includes('hiring') && !title.includes('Ask HN:')) {
      items.push({
        title: title,
        summary: points ? `${points} points | ${description.slice(0, 100)}` : description.slice(0, 150),
        company: 'HackerNews',
        tags: ['Tech'],
        link: link
      });
    }
  }

  return [{ json: { source: 'hackernews', posts: items, count: items.length, error: false, errorMsg: '' } }];
} catch (e) {
  return [{ json: { source: 'hackernews', posts: [], count: 0, error: true, errorMsg: e.message } }];
}

const response = $input.first().json;

try {
  const xmlText = typeof response === 'string' ? response : (response.data || '');

  const items = [];
  // GeekNews uses Atom format with <entry> instead of RSS <item>
  const entryRegex = /<entry>(.*?)<\/entry>/gs;
  let match;

  while ((match = entryRegex.exec(xmlText)) !== null && items.length < 10) {
    const entryXml = match[1];

    // Get title (handles CDATA)
    const getTag = (xml, tag) => {
      const regex = new RegExp(`<${tag}[^>]*><!\\[CDATA\\[([\\s\\S]*?)\\]\\]><\/${tag}>|<${tag}[^>]*>([\\s\\S]*?)<\/${tag}>`, 'i');
      const m = xml.match(regex);
      return m ? (m[1] || m[2] || '').trim() : '';
    };

    // Atom format: <link rel='alternate' type='text/html' href='URL' />
    const getLinkHref = (xml) => {
      const linkMatch = xml.match(/<link[^>]*rel=['"]alternate['"][^>]*href=['"]([^'"]+)['"][^>]*\/?>|<link[^>]*href=['"]([^'"]+)['"][^>]*rel=['"]alternate['"][^>]*\/?>/i);
      return linkMatch ? (linkMatch[1] || linkMatch[2] || '') : '';
    };

    const title = getTag(entryXml, 'title');
    const link = getLinkHref(entryXml);
    // Atom uses <content> instead of <description>
    const content = getTag(entryXml, 'content').replace(/<[^>]*>/g, '').slice(0, 200);

    if (title && link) {
      items.push({
        title: title,
        summary: content,
        company: 'GeekNews',
        tags: ['Tech'],
        link: link
      });
    }
  }

  return [{ json: { source: 'geeknews', posts: items, count: items.length, error: false, errorMsg: '' } }];
} catch (e) {
  return [{ json: { source: 'geeknews', posts: [], count: 0, error: true, errorMsg: e.message } }];
}

const items = $input.all();

let velopers = { posts: [], count: 0, error: false, errorMsg: '' };
let infoq = { posts: [], count: 0, error: false, errorMsg: '' };
let hackernews = { posts: [], count: 0, error: false, errorMsg: '' };
let geeknews = { posts: [], count: 0, error: false, errorMsg: '' };

for (const item of items) {
  if (item.json.source === 'velopers') {
    velopers = item.json;
  } else if (item.json.source === 'infoq') {
    infoq = item.json;
  } else if (item.json.source === 'hackernews') {
    hackernews = item.json;
  } else if (item.json.source === 'geeknews') {
    geeknews = item.json;
  }
}

const hasError = velopers.error && infoq.error && hackernews.error && geeknews.error;
const totalCount = velopers.count + infoq.count + hackernews.count + geeknews.count;
const errorMsg = [velopers.errorMsg, infoq.errorMsg, hackernews.errorMsg, geeknews.errorMsg].filter(Boolean).join('; ');

return [{
  json: {
    velopers: JSON.stringify(velopers.posts, null, 2),
    velopersCount: velopers.count,
    infoq: JSON.stringify(infoq.posts, null, 2),
    infoqCount: infoq.count,
    hackernews: JSON.stringify(hackernews.posts, null, 2),
    hackernewsCount: hackernews.count,
    geeknews: JSON.stringify(geeknews.posts, null, 2),
    geeknewsCount: geeknews.count,
    totalCount: totalCount,
    error: hasError,
    errorMsg: errorMsg
  }
}];
